import axios from 'axios';
import React from 'react'
import {useState,useEffect} from 'react'
import { NavLink } from 'react-router-dom';

export default function Userinfo() {
      const [user, setusers] = useState([]);
console.log(user)
      useEffect(() => {
            loadUsers()

      }, [])

      const loadUsers = async() =>{
             const result = await axios.get("http://localhost:3000/users")

               let res = result.data

            const getEmail =JSON.parse(localStorage.getItem("user-info"))
            const found = res.find(({email}) => email === getEmail.email)
            setusers(found)


      }
      const deleteUser = async id =>{
            await axios.delete(`http://localhost:3000/users/${id}`)
            loadUsers()
      }

      return (
            <div>
            <table className="table">
              <thead className="thead">
                <tr>
                  <th scope="col">Name</th>
                  <th scope="col">Email</th>
                  <th scope="col">profession</th>
                  <th scope="col">dateOfBirth</th>
                  <th scope="col">Action</th>
                </tr>
              </thead>
              <tbody className="tbody">                            
              <tr >                     
                              <td >{user.name}</td>
                              <td>{user.email}</td>
                              <td>{user.profession}</td>
                              <td>{user.dateOfBirth}</td>
                              <td>
                              <NavLink to={`/user/edit/${user.id}`} className="btn btn-success" >Edit</NavLink>
                              <NavLink to={`/user/view/${user.id}`} className="btn btn-info">Info</NavLink>
                              <button onClick = {() => deleteUser(user.id)} className="btn btn-danger">Delete</button>
                              </td>
                        </tr>      
              </tbody>
            </table>
          </div>
      )
}
